from concoord.object.lock import Lock


class MultiElection:

    def __init__(self):
        self.locks = []

    def create_new_lock(self):
        self.locks.append(Lock())
        return len(self.locks)-1

    def acquire_lock(self, index, _concoord_command):
        self.locks[index].acquire( _concoord_command)

    def release_lock(self, index, _concoord_command):
        self.locks[index].release( _concoord_command)
